const path = require('path')

const BASE_PATH = path.join(__dirname, 'server', 'db')

module.exports = {
  test: {
    client: 'pg',
    connection: 'postgres://localhost:5432/ss_test',
    migrations: {
      directory: path.join(BASE_PATH, 'migrations')
    },
    seeds: {
      directory: path.join(BASE_PATH, 'seeds/test')
    }
  },
  development: {
    client: 'pg',
    connection: 'postgres://localhost:5432/ss_dev',
    migrations: {
      directory: path.join(BASE_PATH, 'migrations')
    },
    seeds: {
      directory: path.join(BASE_PATH, 'seeds/development')
    }
  },
  production: {
    client: 'pg',
    connection: 'postgres://localhost:5432/ss',
    migrations: {
      directory: path.join(BASE_PATH, 'migrations')
    },
    seeds: {
      directory: path.join(BASE_PATH, 'seeds/production')
    }
  }
}

