import knex from './connection'

class SimpleCrud {
  constructor (table) {
    this._table = table
  }

  get table () {
    return this._table
  }
  getAll () {
    return knex(this._table).select('*')
  }

  getAllActive () {
    return this.getAll()
      .where('deleted', false)
      .andWhere('active', true)
  }

  get (id) {
    return knex(this._table)
      .select('*')
      .where({id: id})
  }
  add (item) {
    return knex(this._table)
      .insert(item)
      .returning('*')
  }

  upd (id, item) {
    return knex(this._table)
      .update(item)
      .where({id: id})
      .returning('*')
  }

  del (id) {
    return knex(this._table)
      .del()
      .where({id: id})
      .returning('*')
  }
}

class OfficeCrud extends SimpleCrud {
  constructor () {
    super('Office')
    this._person = 'Person'
    this._pOffice = 'PersonOffice'
  }

  getAssignedPersons (office) {
    return knex
      .select('p.*')
      .from(`${this._person} as p`)
      .innerJoin(`${this._pOffice} as po`, 'po.person_id', 'p.id')
      .innerJoin(`${this._table} as o`, 'po.office_id', 'o.id')
      .where('o.id', office.id)
      .andWhere('po.active', true)
      .andWhere('po.deleted', false)
      .andWhere('p.active', true)
      .andWhere('p.deleted', false)
  }
}

export const Office = new OfficeCrud()
export const Person = new SimpleCrud('Person')
export const Position = new SimpleCrud('Position')
export const PersonPosition = new SimpleCrud('PersonPosition')
export const PersonOffice = new SimpleCrud('PersonOffice')
export const PersonTime = new SimpleCrud('PersonTime')

