'use strict'

const v = require('../validations')

module.exports = function(employee) {
  // employee.validatesAbsenceOf('id', v.abs())

  employee.validatesUniquenessOf(
    'ipn',
    v.uniq({
      scopedTo: ['outletId', 'timelistId', 'active'],
    }),
  )

  employee.validatesNumericalityOf('ipn', v.int())

  employee.validatesPresenceOf('lastName', 'name', 'ipn', v.empty())

  employee.validatesLengthOf('lastName', v.len({min: v.NAME_STR_LEN}))

  employee.validatesLengthOf('name', v.len({min: v.NAME_STR_LEN}))

  employee.validate.apply(employee, v.minLenOrNull('sureName', v.NAME_STR_LEN))

  employee.validatesInclusionOf(
    'workShift',
    v.oneOf({
      in: [0, 1, 2],
      allowNull: true,
    }),
  )

  employee.validate.apply(employee, v.isPhone('phone', {allowNull: true}))

  employee.validate.apply(
    employee,
    v.inRange('hours', {allowNull: true, min: 1, max: 24}),
  )

  // format phone number before persist
  employee.observe('persist', function formatPhoneNumber(ctx, next) {
    if (ctx.currentInstance && ctx.currentInstance.phone && ctx.data) {
      const phone = v.formatPhone(ctx.currentInstance.phone)
      ctx.currentInstance.phone = phone
      ctx.data.phone = phone
    }
    next()
  })
}

